var superagent = require('superagent');
var expect = require('expect');

var app = require('./server');

var should = require('should');

describe('routing', function() {
    it("should load index routing file.", function() {
        superagent.get('http://localhost:3000/index').end(function(e, res) {
            expect(e).to.eql(null);
            expect(typeof res.body).to.eql('object');
            expect(res.body.length).to.be.above(0);
            done();
        });
    });
    it("should load users routing file.", function() {
        superagent.get('http://localhost:3000/users').end(function(e, res) {
            expect(e).to.eql(null);
            expect(typeof res.body).to.eql('object');
            expect(res.body.length).to.be.above(0);
            done();
        });
    });

});
